import { KeyringControllerState } from "./IKeyringController";
export class BaseKeyringController<C extends BaseConfig, S extends KeyringControllerState> extends BaseController<C, S> {
  constructor({ config = {}, state }: { config: Partial<C>; state?: Partial<S> }) {
    super({ config, state });
    this.defaultState = {
      wallets: state.wallets ?? [],
    } as S;
    this.initialize();
  }

  // for signing auth message
  signAuthMessage(address: string, message: string): string {
    const keyring = this.state.wallets.find((x) => x.address === address);
    if (!keyring) {
      throw new Error("key does not exist");
    }
    const hashedMessage = hashMessage(message).toString("hex");
    const rawMessageSig = signMessage(keyring.privateKey, hashedMessage);
    return rawMessageSig;
  }
}
import { rpcErrors } from "@metamask/rpc-errors";
import { Block, createAsyncMiddleware, JRPCEngineNextCallback, JRPCMiddleware, JRPCRequest, JRPCResponse, Payload } from "@toruslabs/openlogin-jrpc";

export interface FetchMiddlewareOptions {
  rpcTarget: string;
  originHttpHeaderKey?: string;
}

export interface PayloadwithOrgin extends Payload {
  origin?: string;
}
export interface FetchMiddlewareFromReqOptions extends FetchMiddlewareOptions {
  req: PayloadwithOrgin;
}

export interface FetchConfig {
  fetchUrl: string;
  fetchParams: Record<string, unknown>;
}

const RETRIABLE_ERRORS: string[] = [
  // ignore server overload errors
  "Gateway timeout",
  "ETIMEDOUT",
  // ignore server sent html error pages
  // or truncated json responses
  "failed to parse response body",
  // ignore errors where http req failed to establish
  "Failed to fetch",
];

function checkForHttpErrors(fetchRes: Response): void {
  // check for errors
  switch (fetchRes.status) {
    case 405:
      throw rpcErrors.methodNotFound();

    case 418:
      throw rpcErrors.internal({ message: `Request is being rate limited.` });

    case 503:
    case 504:
      throw rpcErrors.internal({
        message: `Gateway timeout. The request took too long to process.` + `This can happen when querying over too wide a block range.`,
      });

    default:
      break;
  }
}

function timeout(duration: number): Promise<number> {
  return new Promise((resolve) => {
    setTimeout(resolve, duration);
  });
}

function parseResponse(fetchRes: Response, body: Record<string, Block>): Block {
  // check for error code
  if (fetchRes.status !== 200) {
    throw rpcErrors.internal({
      message: `Non-200 status code: '${fetchRes.status}'`,
      data: body,
    });
  }
  // check for rpc error
  if (body.error) {
    throw rpcErrors.internal({
      data: body.error,
    });
  }
  // return successful result
  return body.result;
}

export function createFetchConfigFromReq({ req, rpcTarget, originHttpHeaderKey }: FetchMiddlewareFromReqOptions): FetchConfig {
  const parsedUrl: URL = new URL(rpcTarget);

  // prepare payload
  // copy only canonical json rpc properties
  const payload: Payload = {
    id: req.id,
    jsonrpc: req.jsonrpc,
    method: req.method,
    params: req.params,
  };

  // extract 'origin' parameter from request
  const originDomain: string | undefined = req.origin;

  // serialize request body
  const serializedPayload: string = JSON.stringify(payload);

  // configure fetch params
  const fetchParams = {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: serializedPayload,
  };

  // optional: add request origin as header
  if (originHttpHeaderKey && originDomain) {
    fetchParams.headers[originHttpHeaderKey] = originDomain;
  }

  return { fetchUrl: parsedUrl.href, fetchParams };
}

export function createFetchMiddleware({ rpcTarget, originHttpHeaderKey }: FetchMiddlewareOptions): JRPCMiddleware<string[], Block> {
  return createAsyncMiddleware(async (req: JRPCRequest<string[]>, res: JRPCResponse<unknown>, _next: JRPCEngineNextCallback) => {
    const { fetchUrl, fetchParams } = createFetchConfigFromReq({
      req,
      rpcTarget,
      originHttpHeaderKey,
    });

    // attempt request multiple times
    const maxAttempts = 5;
    const retryInterval = 1000;
    for (let attempt = 0; attempt < maxAttempts; attempt++) {
      try {
        const fetchRes: Response = await fetch(fetchUrl, fetchParams);
        // check for http errrors
        checkForHttpErrors(fetchRes);
        // parse response body
        const fetchBody: Record<string, Block> = await fetchRes.json();
        const result: Block = parseResponse(fetchRes, fetchBody);
        // set result and exit retry loop
        res.result = result;
        return;
      } catch (err) {
        const errMsg: string = err.toString();
        const isRetriable: boolean = RETRIABLE_ERRORS.some((phrase) => errMsg.includes(phrase));
        // re-throw error if not retriable
        if (!isRetriable) {
          throw err;
        }
      }
      // delay before retrying
      await timeout(retryInterval);
    }
  });
}
import { JRPCEngineNextCallback, JRPCRequest, JRPCResponse } from "@toruslabs/openlogin-jrpc";
import log from "loglevel";

export interface LoggerMiddlewareOptions {
  origin: string;
}

export function createLoggerMiddleware(options: LoggerMiddlewareOptions) {
  return function loggerMiddleware(request: JRPCRequest<unknown>, response: JRPCResponse<unknown>, next: JRPCEngineNextCallback): void {
    next((callback) => {
      if (response.error) {
        log.warn("Error in RPC response:\n", response);
      }
      if ((request as unknown as { isTorusInternal: boolean }).isTorusInternal) return;
      log.info(`RPC (${options.origin}):`, request, "->", response);
      callback();
    });
  };
}
import { JRPCEngineNextCallback, JRPCRequest, JRPCResponse } from "@toruslabs/openlogin-jrpc";

export interface OriginMiddlewareOptions {
  origin: string;
}
export function createOriginMiddleware(options: OriginMiddlewareOptions) {
  return function originMiddleware(request: JRPCRequest<unknown>, _: JRPCResponse<unknown>, next: JRPCEngineNextCallback): void {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    (request as any).origin = options.origin;
    next();
  };
}
