    profileImage: "",
    typeOfLogin: LOGIN_PROVIDER.GOOGLE,
    verifier: "",
    verifierId: "",
  },
} as AddressPreferences;

/**
 * Controller that stores shared settings and exposes convenience methods
 */
export abstract class BasePreferencesController<
  P extends AddressPreferences,
  C extends PreferencesConfig,
  S extends PreferencesState<P>
> extends BaseController<C, S> {
  /**
   * Name of this controller used during composition
   */
  name = "PreferencesController";

  public iframeOrigin: string;

  private signAuthMessage: BaseKeyringController<BaseConfig, KeyringControllerState>["signAuthMessage"];

  private defaultPreferences: Partial<P>;

  /**
   * Creates a PreferencesController instance
   *
   * @param config - Initial options used to configure this controller
   * @param state - Initial state to set on this controller
   */
  constructor({
    config,
    state,
    defaultPreferences,
    signAuthMessage,
  }: {
    config?: Partial<C> & Pick<C, "api" | "commonApiHost" | "signInPrefix">;
    state?: Partial<S>;
    defaultPreferences?: Partial<P>;
    signAuthMessage: BaseKeyringController<BaseConfig, KeyringControllerState>["signAuthMessage"];
  }) {
    super({ config, state });
    if (!config.api) {
      throw new Error("PreferencesController - no api specified in config.");
    }
    this.defaultState = {
      identities: {},
      selectedAddress: "",
      lastErrorMessage: "",
      lastSuccessMessage: "",
    } as S;
    this.defaultConfig = {
      api: config.api,
      pollInterval: DEFAULT_INTERVAL,
    } as C;
    this.initialize();
    this.defaultPreferences = {
      ...DEFAULT_PREFERENCES,
      ...defaultPreferences,
    };
    this.signAuthMessage = signAuthMessage;
  }

  public setIframeOrigin(origin: string): void {
    this.iframeOrigin = origin;
  }

  getAddressState(address?: string): P | undefined {
    const selectedAddress = address || this.state.selectedAddress;
    return this.state.identities[selectedAddress];
  }

  /**
   * Sets selected address
   *
   * @param selectedAddress - casper account hash
   */
  setSelectedAddress(selectedAddress: string): void {
    this.update({ selectedAddress } as Partial<S>);
  }

  async getUser<U>(address: string): Promise<U> {
    const user = await get<{ success: boolean; error?: string; data?: U }>(`${this.config.api}/user?fetchTx=false`, this.headers(address), {
      useAPIKey: true,
    });
    return user.data;
  }

  async createUser(params: {
    selectedCurrency: string;
    theme: THEME;
    verifier: string;
    verifierId: string;
    locale: string;
    address: string;
    idToken?: string;
  }): Promise<void> {
    const { selectedCurrency, theme, verifier, verifierId, locale, address, idToken } = params;
    const userPayload: UserPayload = {
      default_currency: selectedCurrency,
      theme,
      verifier,
      verifier_id: verifierId,
      locale,
      idToken,
    };
    await post(`${this.config.api}/user`, userPayload, this.headers(address), { useAPIKey: true });
    this.updateState(
      {
        theme,
        defaultPublicAddress: address,
        selectedCurrency,
        locale,
      } as Partial<P>,
      address
    );
  }

  public async storeUserLogin(params: {
    verifier: string;
    verifierId: string;
    address: string;
    options: { calledFromEmbed: boolean; rehydrate: boolean };
    idToken?: string;
  }): Promise<void> {
    const { verifierId, verifier, options, address, idToken } = params;
    if (!options.rehydrate) {
      const browser = bowser.getParser(window.navigator.userAgent);
      const specialBrowser = getCustomDeviceInfo();
      const recordLoginPayload: RecordLoginPayload = {
        os: browser.getOSName(),
        os_version: browser.getOSVersion() || "unidentified",
        browser: specialBrowser?.browser || browser.getBrowserName() || "unidentified",
        browser_version: browser.getBrowserVersion() || "unidentified",
        platform: browser.getPlatform().type || "desktop",
        hostname: this.iframeOrigin,
        verifier,
        verifier_id: verifierId,
        idToken,
      };
      await post<{ success: boolean }>(`${this.config.api}/user/recordLogin`, recordLoginPayload, this.headers(address), { useAPIKey: true });
    }
  }

  async setCrashReport(isEnabled: boolean): Promise<boolean> {
    if (isEnabled === this.getAddressState()?.crashReport) return true;
    try {
      await patch(`${this.config.api}/user`, { enable_crash_reporter: isEnabled }, this.headers(), { useAPIKey: true });
      this.updateState({ crashReport: isEnabled } as Partial<P>);
      return true;
    } catch (error) {
      log.error(error);
      return false;
    }
  }

  async setUserTheme(theme: THEME): Promise<boolean> {
    if (theme === this.getAddressState()?.theme) return true;
    try {
      await patch(`${this.config.api}/user`, { theme }, this.headers(), { useAPIKey: true });
      this.updateState({ theme } as Partial<P>);
      return true;
    } catch (error) {
      log.error(error);
      return false;
    }
  }

  async setUserLocale(locale: string): Promise<boolean> {
    if (locale === this.getAddressState()?.locale) return;
    try {
      await patch(`${this.config.api}/user`, { locale }, this.headers(), { useAPIKey: true });
      this.updateState({ locale } as Partial<P>);
      return true;
    } catch (error) {
      log.error("unable to set locale", error);
      return false;
    }
  }

  async setSelectedCurrency(payload: { selectedCurrency: string }): Promise<boolean> {
    if (payload.selectedCurrency === this.getAddressState()?.selectedCurrency) return true;
    try {
      await patch(`${this.config.api}/user`, { default_currency: payload.selectedCurrency }, this.headers(), { useAPIKey: true });
      this.updateState({ selectedCurrency: payload.selectedCurrency } as Partial<P>);
      return true;
    } catch (error) {
      log.error(error);
      return false;
    }
  }

  async addContact(contact: ContactPayload): Promise<boolean> {
    try {
      const response = await post<{ success: boolean; data: Contact }>(`${this.config.api}/contact`, contact, this.headers(), { useAPIKey: true });
      this.updateState({ contacts: [...(this.getAddressState()?.contacts || []), response.data] } as Partial<P>);
      return true;
    } catch (error) {
      log.error("unable to add contact", error);
      return false;
    }
  }

  async deleteContact(contactId: number): Promise<boolean> {
    try {
      const response = await remove<{ success: boolean; data: { id: number } }>(`${this.config.api}/contact/${contactId}`, {}, this.headers(), {
        useAPIKey: true,
      });
      const finalContacts = this.getAddressState()?.contacts.filter((contact) => contact.id !== response.data.id);
      if (finalContacts) this.updateState({ contacts: [...finalContacts] } as Partial<P>);
      return true;
    } catch (error) {
      log.error("unable to delete contact", error);
      return false;
    }
  }

  async revokeDiscord(idToken: string): Promise<void> {
    try {
      const resp = await post(`${this.config.api}/revoke/discord`, { token: idToken }, this.headers(), { useAPIKey: true });
      log.info(resp);
    } catch (error) {
      log.error(error);
    }
  }

  async patchPastTx(body: { id: string; status: TransactionStatus; updated_at: string }, address: string): Promise<void> {
    try {
      const response = await patch(`${this.config.api}/transaction`, body, this.headers(address), { useAPIKey: true });
      log.info("successfully patched", response);
    } catch (error) {
      log.error("unable to patch tx", error);
    }
  }

  async postPastTx<T>(tx: T, address: string): Promise<{ success: boolean; response: number[] }> {
    try {
      const response = await post(`${this.config.api}/transaction`, tx, this.headers(address), {
        useAPIKey: true,
      });
      log.info("successfully posted tx", response);
      return response as { success: boolean; response: number[] };
    } catch (error) {
      log.error(error, "unable to insert transaction");
    }
  }

  async getWalletOrders<T>(address: string): Promise<T[]> {
    try {
      const response = await get<{ success: boolean; data: T[] | null }>(`${this.config.api}/transaction`, this.headers(address), {
        useAPIKey: true,
      });
      return response.success ? (response.data ? response.data : []) : [];
    } catch (error) {
      log.error("unable to get wallet orders tx", error);
      return [];
    }
  }

  async getTopUpOrders<T>(address: string): Promise<T[]> {
    try {
      const response = await get<{ success: boolean; data: T[] }>(`${this.config.commonApiHost}/transaction`, this.headers(address), {
        useAPIKey: true,
      });
      return response.data || [];
    } catch (error) {
      log.error("unable to fetch past Top up orders", error);
    }
  }

  async getBillBoardData(): Promise<BillboardEvent[]> {
    try {
      const response = await get<{ success: boolean; data: BillboardEvent[] | null }>(`${this.config.api}/billboard`, this.headers(), {
        useAPIKey: true,
      });
      return response.success ? response.data : [];
    } catch (error) {
      log.error("unable to get billboard data", error);
      return [];
    }
  }

  async getMessageForSigning(publicAddress: string): Promise<string> {
    const response = await post<{ success: boolean; message: string }>(
      `${this.config.api}/auth/message`,
      {
        public_address: publicAddress,
      },
      {},
      { useAPIKey: true }
    );
    return response.message;
  }

  async getTwitterId(payload: { nick: string; typeOfLogin: string }): Promise<string> {
    const res = await get<{ success: boolean; data: string }>(`${this.config.api}/twitter?screen_name=${payload.nick}`, this.headers(), {
      useAPIKey: true,
    });
    return `${payload.typeOfLogin.toLowerCase()}|${res.data.toString()}`;
  }

  async sendEmail(payload: { emailObject: EmailObject }): Promise<void> {
    return post(`${this.config.api}/transaction/sendemail`, payload.emailObject, this.headers(), { useAPIKey: true });
  }

  async refreshJwt(): Promise<void> {
    const address = this.state.selectedAddress;
    const messageToSign = await this.getMessageForSigning(address);
    if (!messageToSign.startsWith(this.config.signInPrefix)) throw new Error("Cannot sign on invalid message");
    const signedMessage = this.signAuthMessage(address, messageToSign);
    const response: { token: string } = await post(
      `${this.config.api}/auth/verify`,
      {
        public_address: address,
        signed_message: signedMessage,
      },
      {},
      { useAPIKey: true }
    );
    this.updateState({ jwtToken: response.token } as Partial<P>, address);
  }

  async getDappList(): Promise<DiscoverDapp[]> {
    try {
      const response = await get<{ success: boolean; error?: string; data?: DiscoverDapp[] }>(`${this.config.api}/dapps`, this.headers(), {
        useAPIKey: true,
      });
      return response.success ? response.data : [];
    } catch (error) {
      log.error("unable to get billboard data", error);
      return [];
    }
  }

  protected async init(address: string, userInfo: UserInfo, jwtToken?: string): Promise<void> {
    let response = { token: jwtToken };
    if (this.getAddressState(address)) return;
    if (!jwtToken) {
      const messageToSign = await this.getMessageForSigning(address);
      if (!messageToSign.startsWith(this.config.signInPrefix)) throw new Error("Cannot sign on invalid message");
      const signedMessage = this.signAuthMessage(address, messageToSign);
      response = await post(
        `${this.config.api}/auth/verify`,
        {
          public_address: address,
          signed_message: signedMessage,
        },
        {},
        { useAPIKey: true }
      );
    }
    this.updateState({ jwtToken: response.token, userInfo } as Partial<P>, address);
  }

  protected updateState(preferences?: Partial<P>, address?: string): P {
    const selectedAddress = address || this.state.selectedAddress;
    const currentState = this.getAddressState(selectedAddress) || cloneDeep(this.defaultPreferences);
    const mergedState = { ...currentState, ...preferences } as P;
    this.update({
      identities: {
        ...this.state.identities,
        [selectedAddress]: mergedState,
      },
    } as S);
    return mergedState;
  }

  private headers(address?: string): { headers: { Authorization: string; "Content-Type": string } } {
    const selectedAddress = address || this.state.selectedAddress;
    return getHeaders(this.getAddressState(selectedAddress)?.jwtToken || "");
  }

  abstract sync(address: string): Promise<boolean>;
}
import { JRPCRequest } from "@toruslabs/openlogin-jrpc";

import { BaseConfig, BaseState } from "../interfaces";

/**
 * The status of the transaction. Each status represents the state of the transaction internally
 * in the wallet. Some of these correspond with the state of the transaction on the network, but
 * some are wallet-specific.
 */

export enum TransactionStatus {
  approved = "approved",
  cancelled = "cancelled",
  confirmed = "confirmed",
  failed = "failed",
  finalized = "finalized",
  processed = "processed",
  rejected = "rejected",
  signed = "signed",
  submitted = "submitted",
  unapproved = "unapproved",
  dropped = "dropped",
  expired = "expired",
}

export const TRANSACTION_TYPES = {
  CONTRACT_INTERACTION: "contractInteraction",
  DEPLOY_CONTRACT: "contractDeployment",
  WASM_BASED_DEPLOY: "wasmBasedDeploy",
  STANDARD_TRANSACTION: "transaction",
  STANDARD_PAYMENT_TRANSACTION: "payment_transaction", // specific to chains like solana and casper
};

export type TRANSACTION_TYPE = (typeof TRANSACTION_TYPES)[keyof typeof TRANSACTION_TYPES];

export const TX_EVENTS = {
  TX_WARNING: "tx:warning",
  TX_ERROR: "tx:error",
  TX_FAILED: "tx:failed",
  TX_CONFIRMED: "tx:confirmed",
  TX_DROPPED: "tx:dropped",
  TX_EXPIRED: "tx:expired",
  TX_STATUS_UPDATE: "tx:status_update",
  TX_UNAPPROVED: "tx:unapproved",
};

export interface TransactionMeta<T> {
  id: string;
  transaction: T;
  transactionType: TRANSACTION_TYPE;
  dappSuggestedTransaction: Partial<T>;
  chainId: string;
  origin: string;
  time: number;
  status: TransactionStatus;
  updated_at?: string; // iso date string
  transactionHash?: string;
  rawTransaction?: string;
  txReceipt?: unknown;
  error?: Error;
  warning?: {
    error?: string;
    message?: string;
  };
}

export type BASE_TX_EVENT_TYPE = {
  txId: string;
};

export type TX_ERROR_EVENT_TYPE = BASE_TX_EVENT_TYPE & {
  error: Error;
};

export type TX_FAILED_EVENT_TYPE = BASE_TX_EVENT_TYPE & {
  error: Error;
};

export type TX_EXPIRED_EVENT_TYPE = BASE_TX_EVENT_TYPE;

export type TX_WARNING_EVENT_TYPE<T> = BASE_TX_EVENT_TYPE & {
  txMeta: TransactionMeta<T>;
};

export type TX_CONFIRMED_EVENT_TYPE = BASE_TX_EVENT_TYPE & {
  txReceipt: unknown;
};

export type TX_DROPPED_EVENT_TYPE = BASE_TX_EVENT_TYPE;

export type TX_STATUS_UPDATE_EVENT_TYPE = BASE_TX_EVENT_TYPE & {
  status: TransactionStatus;
};

export type TX_FINISHED_EVENT_TYPE<T> = BASE_TX_EVENT_TYPE & {
  txMeta: TransactionMeta<T>;
};

/**
 * Transaction controller configuration
 */
export interface TransactionConfig extends BaseConfig {
  txHistoryLimit: number;
}

/**
 * Transaction controller state
 */
export interface TransactionState<T> extends BaseState {
  /**
   * Transactions by key (id) value (TxMeta)
   */
  transactions: Record<string, TransactionMeta<T>>; // all transactions (unapprroved and all others)
  unapprovedTxs?: Record<string, TransactionMeta<T>>; // only unapproved
  currentNetworkTxsList?: TransactionMeta<T>[];
}

/**
 * Result
 *
 * result - Promise resolving to a new transaction hash
 * transactionMeta - Meta information about this new transaction
 */
export interface Result<T> {
  result: Promise<string>;
  transactionMeta: TransactionMeta<T>;
}

export interface ITransactionController<T> {
  /**
   * Add a new unapproved transaction to state.
   * @param transaction - Transaction object to add
   * @returns - Object containing a promise resolving to the transaction hash if approved
   */
  addTransaction(transaction: T, req: JRPCRequest<T & { windowId?: string }> & { origin: string }): Promise<Result<T>>;

  /**
   * Approves a transaction and updates it's status in state.
   *
   * @param transactionID - ID of the transaction to approve
   * @returns - Promise resolving when this operation completes
   */
  approveTransaction(transactionID: string): Promise<void>;

  /**
   * Cancels a transaction based on its ID by setting its status to "rejected"
   *
   * @param transactionID - ID of the transaction to cancel
   */
  cancelTransaction?(transactionID: string): Promise<void>;
}

export interface ITransactionStateManager<T> {
  addTransactionToState(txMeta: TransactionMeta<T>): TransactionMeta<T>;

  wipeTransactions(address: string): void;

  getTransactions(args: {
    searchCriteria?: Record<string, (val: unknown) => boolean> | Record<string, unknown>;
    initialList?: TransactionMeta<T>[];
    filterToCurrentNetwork?: boolean;
  }): TransactionMeta<T>[];
  getApprovedTransactions(address?: string): TransactionMeta<T>[];
  getPendingTransactions(address?: string): TransactionMeta<T>[];
  getConfirmedTransactions(address?: string): TransactionMeta<T>[];
}
import { omitBy, pickBy } from "lodash";

import BaseController from "../BaseController";
import { transactionMatchesNetwork } from "../utils";
import {
  TransactionConfig,
  TransactionMeta,
  TransactionState,
  TransactionStatus,
  TX_EVENTS,
  TX_STATUS_UPDATE_EVENT_TYPE,
} from "./ITransactionController";

export class BaseTransactionStateManager<T> extends BaseController<TransactionConfig, TransactionState<T>> {
  protected getCurrentChainId: () => string;

  constructor({
    config,
    state,
    getCurrentChainId,
  }: {
    config?: Partial<TransactionConfig>;
    state?: Partial<TransactionState<T>>;
    getCurrentChainId: () => string;
  }) {
    super({ config, state });
    this.defaultConfig = {
      txHistoryLimit: 40,
    };
    this.defaultState = {
      transactions: {},
      unapprovedTxs: {},
      currentNetworkTxsList: [],
    };
    this.initialize();
    this.getCurrentChainId = getCurrentChainId;
  }

  getUnapprovedTxList(): Record<string, TransactionMeta<T>> {
    const chainId = this.getCurrentChainId();
    return pickBy(
      this.state.transactions,
      (transaction) => transaction.status === TransactionStatus.unapproved && transactionMatchesNetwork<T>(transaction, chainId)
    );
  }

  getTransaction(txId: string): TransactionMeta<T> {
    const { transactions } = this.state;
    return transactions[txId];
  }

  updateTransaction(txMeta: TransactionMeta<T>): void {
    // commit txMeta to state
    const txId = txMeta.id;
    txMeta.updated_at = new Date().toISOString();
    this.update({
      transactions: {
        ...this.state.transactions,
        [txId]: txMeta,
      },
    });
  }

  setTxStatusRejected(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.rejected);
    this._deleteTransaction(txId);
  }

  /**
   * The implementing controller can override this functionality and add custom logic + call super.()
   */
  setTxStatusUnapproved(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.unapproved);
  }

  setTxStatusApproved(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.approved);
  }

  setTxStatusSigned(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.signed);
  }

  setTxStatusSubmitted(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.submitted);
  }

  setTxStatusDropped(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.dropped);
  }

  setTxStatusExpired(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.expired);
  }

  setTxStatusConfirmed(txId: string): void {
    this._setTransactionStatus(txId, TransactionStatus.confirmed);
  }

  setTxStatusFailed(txId: string, error_: Error): void {
    const error = !error_ ? new Error("Internal torus failure") : error_;

    const txMeta = this.getTransaction(txId);
    txMeta.error = error;
    this.updateTransaction(txMeta);
    this._setTransactionStatus(txId, TransactionStatus.failed);
  }

  /**
   * Method to determine if the transaction is in a final state
   * @param status - Transaction status
   * @returns boolean if the transaction is in a final state
   */
  isFinalState(status: TransactionStatus): boolean {
    return (
      status === TransactionStatus.rejected ||
      status === TransactionStatus.submitted ||
      status === TransactionStatus.confirmed ||
      status === TransactionStatus.failed ||
      status === TransactionStatus.cancelled ||
      status === TransactionStatus.expired
    );
  }

  /**
   * Filters out the unapproved transactions from state
   */
  clearUnapprovedTxs(): void {
    this.update({
      transactions: omitBy(this.state.transactions, (transaction: TransactionMeta<T>) => transaction.status === TransactionStatus.unapproved),
    });
  }

  /**
   * will append new transactions to old txns.
   */
  _addTransactionsToState(transactions: TransactionMeta<T>[]): void {
    this.update({
      transactions: transactions.reduce((result: Record<string, TransactionMeta<T>>, newTx: TransactionMeta<T>) => {
        result[newTx.id] = newTx;
        return result;
      }, this.state.transactions),
    });
  }

  /**
   * will set new txns, override existing if any in state.
   */
  _setTransactionsToState(transactions: TransactionMeta<T>[]): void {
    this.update({
      transactions: transactions.reduce((result: Record<string, TransactionMeta<T>>, newTx: TransactionMeta<T>) => {
        result[newTx.id] = newTx;
        return result;
      }, {}),
    });
  }

  _deleteTransaction(targetTransactionId: string): void {
    const { transactions } = this.state;
    delete transactions[targetTransactionId];
    this.update({
      transactions,
    });
  }

  _deleteTransactions(targetTransactionIds: string[]): void {
    const { transactions } = this.state;
    targetTransactionIds.forEach((transactionId) => {
      delete transactions[transactionId];
    });
    this.update({
      transactions,
    });
  }

  protected _setTransactionStatus(txId: string, status: TransactionStatus): void {
    const txMeta = this.getTransaction(txId);
    if (!txMeta) {
      return;
    }
    txMeta.status = status;
    // only updating status so no validation required on txn.
    this.updateTransaction(txMeta);
    this.emit(TX_EVENTS.TX_STATUS_UPDATE, { txId, status } as TX_STATUS_UPDATE_EVENT_TYPE);
    if (this.isFinalState(status)) {
      this.emit(`${txMeta.id}:finished`, txMeta);
    } else {
      this.emit(`${txMeta.id}:${status}`, txId);
    }
  }
}
